
-- Tabela para armazenar os jogos
CREATE TABLE tb_games (
                          id SERIAL PRIMARY KEY,
                          title VARCHAR(255) NOT NULL,
                          score DECIMAL(2, 1) NOT NULL,
                          game_year INT NOT NULL,
                          genre VARCHAR(255) NOT NULL,
                          platforms VARCHAR(255) NOT NULL,
                          img_url TEXT NOT NULL,
                          short_description TEXT NOT NULL,
                          long_description TEXT NOT NULL
);

-- Tabela para listas de jogos
CREATE TABLE tb_game_list (  id SERIAL PRIMARY KEY,  name VARCHAR(255) NOT NULL);

-- Tabela de pertencimento (relacionamento entre listas e jogos)
CREATE TABLE tb_belonging (
                              list_id INT NOT NULL,
                              game_id INT NOT NULL,
                              position INT NOT NULL,
                              PRIMARY KEY (list_id, game_id),
                              FOREIGN KEY (list_id) REFERENCES tb_list(id) ON DELETE CASCADE,
                              FOREIGN KEY (game_id) REFERENCES tb_games(id) ON DELETE CASCADE
);





